/* ===== SHA256 with Crypto-js ===============================
|  Learn more: Crypto-js: https://github.com/brix/crypto-js  |
|  =========================================================*/

const SHA256 = require('crypto-js/sha256')

// Configure LevelDB to persist blockchain data
const level = require('level')
const chainDB = './databases/chaindata'
const db = level(chainDB)

// import util libs
const util = require('./util')

// import block class
const Block = require('../models/Block')

/* ===== Blockchain Class ==========================
|  Class with a constructor for new blockchain 		|
|  ================================================*/

class Blockchain {
  constructor () {
    // CRITERION: Genesis block persist as the first block in the blockchain using LevelDB.
    this.getBlockHeight().then(height => {
      // console.log(height)  // DEBUG
      if (height === -1)
        this.addBlock(new Block('Genesis block')).then(() => console.log('Genesis block stored!'))
    })
  }

  // Add new block
  // CRITERION: addBlock(newBlock) function includes a method to store newBlock with LevelDB.
  async addBlock (newBlock) {
    // previous block height
    let previousBlockHeight = parseInt(await this.getBlockHeight())
    // Block height
    newBlock.height = previousBlockHeight + 1
    // UTC timestamp
    newBlock.time = new Date()
      .getTime()
      .toString()
      .slice(0, -3)
    // previous block hash
    if (newBlock.height > 0) {
      let previousBlock = await this.getBlock(previousBlockHeight)
      newBlock.previousBlockHash = previousBlock.hash
    }
    // Block hash with SHA256 using newBlock and converting to a string
    newBlock.hash = SHA256(JSON.stringify(newBlock)).toString()

    // Adding block object to levelDB
    await this.addLevelDBData(newBlock.height, JSON.stringify(newBlock))

    // return the new block
    return newBlock
  }

  // Get block height
  // CRITERION: Modify getBlockHeight() function to retrieve current block height within the LevelDB chain.
  async getBlockHeight () {
    return await this.getBlockHeightLevel()
  }

  // get block
  // CRITERION: Modify getBlock() function to retrieve a block by it's block heigh within the LevelDB chain.
  async getBlock (blockHeight) {
    let block = JSON.parse(await this.getLevelDBData(blockHeight))

    // verify if ins't the Genesis block
    if (parseInt(block.height) > 0) {
      block.body.star.storyDecoded = Buffer.from(block.body.star.story, 'hex').toString()
    }
    // return object
    return block
  }

  // get block by address
  // CRITERION: Get endpoint with URL parameter for wallet address
  async getBlocksByAddress (address) {
    const blocks = []
    let block = {}

    return new Promise((resolve, reject) => {
      db.createReadStream()
        .on('data', data => {
          if (data.key !== 0) {
            block = JSON.parse(data.value)

            if (block.body.address === address) {
              block.body.star.storyDecoded = Buffer.from(block.body.star.story, 'hex').toString()
              blocks.push(block)
            }
          }
        })
        .on('error', error => {
          return reject(error)
        })
        .on('close', () => {
          return resolve(blocks)
        })
    })
  }

  // get block by address
  // CRITERION: Get endpoint with URL parameter for star block hash JSON Response
  async getBlockByHash (hash) {
    let block = {}

    return new Promise((resolve, reject) => {
      db.createReadStream()
        .on('data', data => {
          block = JSON.parse(data.value)

          if (util.isStringChainEquals(block.hash, hash)) {
            if (data.key !== 0) {
              block.body.star.storyDecoded = Buffer.from(block.body.star.story, 'hex').toString()
              return resolve(block)
            } else {
              return resolve(block)
            }
          }
        })
        .on('error', error => {
          return reject(error)
        })
        .on('close', () => {
          return reject('Block not found')
        })
    })
  }

  // validate block
  // CRITERION: Modify the validateBlock() function to validate a block stored within levelDB
  async validateBlock (blockHeight) {
    // get block object
    let block = await this.getBlock(blockHeight)
    // get block hash
    let blockHash = block.hash
    // remove block hash to test block integrity
    block.hash = ''

    // generate block hash
    let validBlockHash = SHA256(JSON.stringify(block)).toString()

    // Compare
    if (blockHash === validBlockHash) {
      // return true if block is valid
      return true
    } else {
      console.log('Block #' + blockHeight + ' invalid hash:\n' + blockHash + '<>' + validBlockHash)
      return false
    }
  }

  // Validate blockchain
  // CRITERION: Modify the validateChain() function to validate blockchain stored within levelDB
  async validateChain () {
    let errorLog = []
    let blockChainHeight = await this.getBlockHeight()

    for (let i = 0; i < blockChainHeight; i++) {
      // validate a single block
      if (!this.validateBlock(i)) errorLog.push(i)

      // compare blocks hash link
      let blockHash = this.getBlock(i).hash
      let previousHash = this.getBlock(i + 1).previousBlockHash
      if (blockHash !== previousHash) {
        errorLog.push(i)
      }
    }

    if (errorLog.length > 0) {
      console.log('Block errors = ' + errorLog.length)
      console.log('Blocks: ' + errorLog)
    } else {
      console.log('No errors detected')
    }
  }

  /* ===== level db methods =====================================
  |  Methods responsible for persisting data     		          |
  |  Learn more: level: https://github.com/Level/level      	|
  |  ==========================================================*/

  getBlockHeightLevel () {
    return new Promise((resolve, reject) => {
      let height = -1
      db.createReadStream()
        .on('data', () => {
          height++
        })
        .on('error', err => {
          console.log('Unable to read data stream!', err)
          reject(err)
        })
        .on('close', () => {
          // console.log('Blockchain height is #' + height) // DEBUG
          resolve(height)
        })
    })
  }

  // Add data to levelDB with key/value pair
  addLevelDBData (key, value) {
    return new Promise((resolve, reject) => {
      db.put(key, value, err => {
        if (err) {
          console.log('Block ' + key + ' submission failed', err)
          reject(err)
        } else {
          console.log('Block #' + key + ' stored')
          resolve(value)
        }
      })
    })
  }

  // Get data from levelDB with key
  getLevelDBData (key) {
    return new Promise((resolve, reject) => {
      db.get(key, (err, value) => {
        if (err) {
          console.log('Not found!', err)
          reject(err)
        } else {
          // console.log('Value = ' + value)  // DEBUG
          resolve(value)
        }
      })
    })
  }
}

module.exports = Blockchain

// Configure LevelDB to access stars data
const level = require('level')
const chainDB = './databases/starchaindata'
const db = level(chainDB)
const defaultWindow = 300 // five minutes

// lib to validate message
const bitcoinMessage = require('bitcoinjs-message')

/**
 * Return current time.
 *
 * @returns {int} Return current timestamp
 * */
function getCurrentTimeStamp () {
  return new Date().getTime()
}

/**
 * Return the message created.
 * @param {String} address The address
 *
 * @returns {String} Return message with format [walletAddress]:[timeStamp]:starRegistry
 * */
function generateMessage (address) {
  return `${address}:${getCurrentTimeStamp()}:starRegistry`
}

/**
 * Check if request time stamp of value has expired.
 * @param {String} requestTimeStamp The timestamp from request
 *
 * @returns {Boolean} Return true if the timestamp from request is minus that five minutes
 * */
function isExpired (requestTimeStamp) {
  // time left < five minutes
  return requestTimeStamp < Date.now() - 5 * 60 * 1000
}

// Util file to manage validation/signature
const validateUtil = {
  // Check if signature is a signature valid
  async isValidSignature (address) {
    return db
      .get(address)
      .then(value => {
        value = JSON.parse(value)
        return value.messageSignature === 'valid'
      })
      .catch(() => {
        throw new Error('Not authorized')
      })
  },

  // Remove signature from leveldb
  invalidateSignature (address) {
    db.del(address)
  },

  // CRITERION: User obtains a response in JSON format with a message to sign.
  saveRequestStarValidation: address => {
    const message = generateMessage(address)

    // CRITERION: Response should contain message details, request timestamp, and time remaining for validation window.
    const data = {
      address: address,
      message: message,
      requestTimeStamp: getCurrentTimeStamp(),
      validationWindow: defaultWindow
    }

    db.put(data.address, JSON.stringify(data))

    return data
  },

  // Save/return request star validation
  async getPendingAddress (address) {
    return new Promise((resolve, reject) => {
      db.get(address, (error, value) => {
        if (value === undefined) {
          return reject(new Error('Not found'))
        } else if (error) {
          return reject(error)
        }

        value = JSON.parse(value)

        // CRITERION: When re-submitting within validation window, validation window should reduce until it expires.
        if (isExpired(value.requestTimeStamp)) {
          resolve(this.saveRequestStarValidation(address))
        } else {
          // CRITERION: Response should contain message details, request timestamp, and time remaining for validation window.
          const data = {
            address: address,
            message: value.message,
            requestTimeStamp: value.requestTimeStamp,
            // CRITERION: The request must be configured with a limited validation window of five minutes.
            validationWindow: Math.floor(
              (value.requestTimeStamp - (Date.now() - 5 * 60 * 1000)) / 1000
            )
          }

          resolve(data)
        }
      })
    })
  },

  // CRITERION: Web API post endpoint validates message signature with JSON response.
  async validateMessageSignature (address, signature) {
    return new Promise((resolve, reject) => {
      db.get(address, (error, value) => {
        if (value === undefined) {
          return reject(new Error('Not found'))
        } else if (error) {
          return reject(error)
        }

        value = JSON.parse(value)

        if (value.messageSignature === 'valid') {
          return resolve({
            registerStar: true,
            status: value
          })
        } else {
          let isValid = false

          // Check if request timeStamp is < that five minutes
          if (isExpired(value.requestTimeStamp)) {
            // Set validation window to zero
            value.validationWindow = 0
            // Inform expired validation message
            value.messageSignature = 'Validation expired!'
          } else {
            // CRITERION: The request must be configured with a limited validation window of five minutes.
            value.validationWindow = Math.floor(
              (value.requestTimeStamp - (Date.now() - 5 * 60 * 1000)) / 1000
            )

            try {
              // CRITERION: The application will validate their request and grant access to register a star.
              isValid = bitcoinMessage.verify(value.message, address, signature)
            } catch (error) {
              isValid = false
            }

            value.messageSignature = isValid ? 'valid' : 'invalid'
          }

          db.put(address, JSON.stringify(value))

          return resolve({
            registerStar: !isExpired(value.requestTimeStamp) && isValid,
            status: value
          })
        }
      })
    })
  }
}

module.exports = validateUtil

const util = {
  /**
   * Verify if the object is empty
   *
   * @param {Object} obj  Object to verify
   * @returns {Boolean} Return true if the object is empty
   */
  empty: obj => {
    if (obj === true) return false
    for (let key in obj) {
      if (obj.hasOwnProperty(key)) return false
    }
    return true
  },

  /**
   * Verify if the object is ASCII
   *
   * @param {String} str  String to verify
   * @returns {Boolean} Return true if the object is ASCII
   */
  isASCII: str => {
    return /^[\x00-\x7F]*$/.test(str)
  },

  /**
   * Check if two strings are equal avoiding timing attacks.
   * See more in: https://snyk.io/blog/node-js-timing-attack-ccc-ctf
   *
   * @param {String} a  First string of verification
   * @param {String} b  First string of verification
   * @returns {Boolean} Returns true if the two strings are exactly the same
   */
  isStringChainEquals: (a, b) => {
    let mismatch = 0
    for (let i = 0; i < a.length; ++i) {
      mismatch |= a.charCodeAt(i) ^ b.charCodeAt(i)
    }
    return !mismatch
  }
}

module.exports = util

/* ===== Block Class ==============================
|  Class with a constructor for block 			   |
|  ===============================================*/

class Block {
  constructor (data) {
    this.hash = ''
    this.height = 0
    this.body = data
    this.time = 0
    this.previousBlockHash = ''
  }
}
module.exports = Block

// import dependencies
const Blockchain = require('./helpers/Blockchain')
const blockchain = new Blockchain()
const Block = require('./models/Block')

// import validator
const { check, validationResult } = require('express-validator/check')

// import util libs
const validateUtil = require('./helpers/ValidationUtil')
const util = require('./helpers/util')

const express = require('express')
const app = express()

app.use(express.json())
app.use(express.urlencoded({ extended: false }))

app.listen(8000, () => console.log('Server started and listening on port 8000!'))

//----------------------------------------------------------------------------------------------------------------------
// URL post http://localhost:8000/requestValidation
// Validate User Request
app.post(
  '/requestValidation',
  [
    // address must be required
    check('address')
      .not()
      .isEmpty()
  ],
  async (req, res) => {
    // check data entries
    const errors = validationResult(req)
    if (!errors.isEmpty()) {
      return res.status(422).json({ errors: errors.array() })
    }

    let data = {}

    try {
      data = await validateUtil.getPendingAddress(req.body.address)
    } catch (err) {
      console.log(err)
      data = await validateUtil.saveRequestStarValidation(req.body.address)
    }

    res.json(data)
  }
)

//----------------------------------------------------------------------------------------------------------------------
// URL post http://localhost:8000/message-signature/validate
// Allow User Message Signature
app.post(
  '/message-signature/validate',
  [
    // address must be required
    check('address')
      .not()
      .isEmpty(),
    // signature must be required
    check('signature')
      .not()
      .isEmpty()
  ],
  async (req, res) => {
    // check data entries
    const errors = validationResult(req)
    if (!errors.isEmpty()) {
      return res.status(422).json({ errors: errors.array() })
    }

    try {
      const response = await validateUtil.validateMessageSignature(
        req.body.address,
        req.body.signature
      )

      if (response.registerStar) {
        res.json(response)
      } else {
        res.status(401).json(response)
      }
    } catch (error) {
      res.status(404).json({
        status: 404,
        message: error.message
      })
    }
  }
)

//----------------------------------------------------------------------------------------------------------------------
// URL post http://localhost:8000/stars/address:address
// Blockchain Wallet Address
app.get('/stars/address:address', async (req, res) => {
  blockchain
    .getBlocksByAddress(req.params.address.slice(1))
    .then(success => {
      res.send(success)
    })
    .catch(() => {
      res.status(404).json({
        status: 404,
        message: 'Block not found'
      })
    })
})

//----------------------------------------------------------------------------------------------------------------------
// URL post http://localhost:8000/stars/hash:hash
// Star Block Hash
app.get('/stars/hash:hash', async (req, res) => {
  blockchain
    .getBlockByHash(req.params.hash.slice(1))
    .then(success => {
      res.send(success)
    })
    .catch(() => {
      res.status(404).json({
        status: 404,
        message: 'Block not found'
      })
    })
})

//----------------------------------------------------------------------------------------------------------------------
// URL get http://localhost:8000/block/{BLOCK_HEIGHT}
// Star Block Height
app.get('/block/:blockHeight', (req, res) => {
  blockchain
    .getBlock(req.params.blockHeight)
    .then(success => {
      // The block contents must respond to GET request with block contents in JSON format
      res.json(success)
    })
    .catch(() => {
      res.status(404).json({
        status: 404,
        message: 'Block not found'
      })
    })
})

//----------------------------------------------------------------------------------------------------------------------
// URL post http://localhost:8000/block
// Post block in blockchain - Star Registration Endpoint
app.post(
  '/block',
  [
    // address must be required
    check('address')
      .not()
      .isEmpty(),
    // star must be required
    check('star').custom(star => {
      // CRITERION: Star object and properties are stored within the body of the block.
      // CRITERION: Star properties include the coordinates with encoded story.
      // CRITERION: Star story supports ASCII text, limited to 250 words (500 bytes), and hex encoded.
      if (util.empty(star)) {
        return Promise.reject('Star object is required')
      }
      if (util.empty(star.ra)) {
        return Promise.reject('Ra is required')
      }
      if (util.empty(star.dec)) {
        return Promise.reject('Dec is required')
      }
      if (util.empty(star.story)) {
        return Promise.reject('Story is required')
      }
      if (star.story.length > 500) {
        return Promise.reject('Story is limited to 500 words. Maximum size is 500 bytes')
      }
      if (!util.isASCII(star.story)) {
        return Promise.reject('Story contains non-ASCII symbols')
      }
      return true
    })
  ],
  async (req, res) => {
    // check data entries
    const errors = validationResult(req)
    if (!errors.isEmpty()) {
      return res.status(422).json({ errors: errors.array() })
    }

    try {
      // Deny registration of a block if he/she did not send a valid signature
      const isValid = await validateUtil.isValidSignature(req.body.address)

      if (!isValid) {
        throw new Error('Signature is not valid')
      }
    } catch (error) {
      res.status(401).json({
        status: 401,
        message: error.message
      })

      return
    }

    // CRITERION: Star story must be encoded to hex before adding the block.
    let star = req.body.star
    // Encode the star story to hex here before making the block.
    star.story = Buffer.from(req.body.star.story).toString('hex')

    // organize the block to create
    const block = {
      address: req.body.address,
      star: star
    }

    blockchain
      .addBlock(new Block(block))
      .then(success => {
        // Override the previous request to prevent the user from placing additional blocks.
        // He/she should return to request Validation and sign a new message in order to add another block.
        validateUtil.invalidateSignature(req.body.address)

        // Note: addBlock method was modified to return the block created
        res.status(201).send(success)
      })
      .catch(() => {
        // return a message in json format with error
        res.json({ error: 'There was an error generating a new block' })
      })
  }
)
//----------------------------------------------------------------------------------------------------------------------
// Send 404 erro, route not found
app.get('*', (req, res) => {
  res.status(404).json({
    status: 404,
    message: 'Route not found. Check documentation.'
  })
})
